import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [youtubeLink, setYoutubeLink] = useState('https://www.youtube.com/watch?v=D9Ihs241zeg');
  const [videoId, setVideoId] = useState('');
  const [captions, setCaptions] = useState({});
  // Changed from array to object with primary, secondary, tertiary keys
  const [selectedLanguages, setSelectedLanguages] = useState({
    primary: 'en',
    secondary: '',
    tertiary: ''
  });
  const [availableLanguages, setAvailableLanguages] = useState([]);
  const [status, setStatus] = useState('');

  // Customization options
  const [captionPlacement, setCaptionPlacement] = useState('Below Video');
  const [nounColor, setNounColor] = useState('Electric Blue');
  const [verbColor, setVerbColor] = useState('Cherry Red');
  const [adjectiveColor, setAdjectiveColor] = useState('Forest Green');
  const [fontSize, setFontSize] = useState('Medium');
  const [fontFamily, setFontFamily] = useState('Sans Serif');

  // Extract video ID from YouTube URL
  const extractVideoId = (url) => {
    const regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[7].length === 11) ? match[7] : null;
  };

  // Initialize with the default URL
  useEffect(() => {
    const id = extractVideoId(youtubeLink);
    if (id) {
      setVideoId(id);
      fetchSampleCaptions(id);
    }
  }, [youtubeLink]);

  // Handle YouTube link input
  const handleLinkChange = (e) => {
    setYoutubeLink(e.target.value);
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const id = extractVideoId(youtubeLink);
    if (id) {
      setVideoId(id);
      setStatus('Loading captions...');
      // For demo purposes, we'll use sample data
      fetchSampleCaptions(id);
    } else {
      setStatus('Invalid YouTube URL');
    }
  };

  // Sample data - in a real app, this would be an API call
  const fetchSampleCaptions = (id) => {
    // Simulate API call delay
    setTimeout(() => {
      // Sample data for TED Talk "The danger of a single story" by Chimamanda Ngozi Adichie
      if (id) {
        // Sample captions data with timestamps and part-of-speech tags
        const sampleCaptions = {
          en: [
            { start: 0, end: 4.5, text: "I'm a <noun>storyteller</noun>. And I would like to <verb>tell</verb> you a few <adjective>personal</adjective> stories about what I like to call 'the danger of the single story.'" },
            { start: 4.5, end: 9.8, text: "I <verb>grew up</verb> on a <noun>university campus</noun> in <adjective>eastern</adjective> Nigeria." },
            { start: 9.8, end: 15.2, text: "My <noun>mother</noun> <verb>says</verb> that I <verb>started</verb> <noun>reading</noun> at the <noun>age</noun> of two, although I <verb>think</verb> four is probably <adjective>close</adjective> to the truth." },
          ],
          de: [
            { start: 0, end: 4.5, text: "Ich bin eine <noun>Geschichtenerzählerin</noun>. Und ich möchte Ihnen einige <adjective>persönliche</adjective> Geschichten <verb>erzählen</verb> über das, was ich 'die Gefahr einer einzigen Geschichte' nenne." },
            { start: 4.5, end: 9.8, text: "Ich <verb>wuchs auf</verb> auf einem <noun>Universitätscampus</noun> im <adjective>östlichen</adjective> Nigeria." },
            { start: 9.8, end: 15.2, text: "Meine <noun>Mutter</noun> <verb>sagt</verb>, dass ich mit zwei Jahren zu <verb>lesen</verb> <verb>begann</verb>, obwohl ich <verb>denke</verb>, dass vier wahrscheinlich <adjective>näher</adjective> an der Wahrheit ist." },
          ],
          fr: [
            { start: 0, end: 4.5, text: "Je suis une <noun>conteuse</noun>. Et j'aimerais vous <verb>raconter</verb> quelques histoires <adjective>personnelles</adjective> sur ce que j'appelle 'le danger d'une histoire unique'." },
            { start: 4.5, end: 9.8, text: "J'ai <verb>grandi</verb> sur un <noun>campus universitaire</noun> dans l'<adjective>est</adjective> du Nigeria." },
            { start: 9.8, end: 15.2, text: "Ma <noun>mère</noun> <verb>dit</verb> que j'ai <verb>commencé</verb> à <verb>lire</verb> à l'<noun>âge</noun> de deux ans, bien que je <verb>pense</verb> que quatre ans est probablement plus <adjective>proche</adjective> de la vérité." },
          ],
          es: [
            { start: 0, end: 4.5, text: "Soy una <noun>narradora</noun>. Y me gustaría <verb>contarles</verb> algunas historias <adjective>personales</adjective> sobre lo que me gusta llamar 'el peligro de la historia única'." },
            { start: 4.5, end: 9.8, text: "<verb>Crecí</verb> en un <noun>campus universitario</noun> en el <adjective>este</adjective> de Nigeria." },
            { start: 9.8, end: 15.2, text: "Mi <noun>madre</noun> <verb>dice</verb> que <verb>comencé</verb> a <verb>leer</verb> a la <noun>edad</noun> de dos años, aunque <verb>creo</verb> que cuatro está probablemente más <adjective>cerca</adjective> de la verdad." },
          ],
          ru: [
            { start: 0, end: 4.5, text: "Я <noun>рассказчица</noun>. И я хотела бы <verb>рассказать</verb> вам несколько <adjective>личных</adjective> историй о том, что я называю 'опасностью одной истории'." },
            { start: 4.5, end: 9.8, text: "Я <verb>выросла</verb> в <noun>университетском городке</noun> в <adjective>восточной</adjective> Нигерии." },
            { start: 9.8, end: 15.2, text: "Моя <noun>мать</noun> <verb>говорит</verb>, что я <verb>начала</verb> <verb>читать</verb> в <noun>возрасте</noun> двух лет, хотя я <verb>думаю</verb>, что четыре года, вероятно, <adjective>ближе</adjective> к истине." },
          ],
          ar: [
            { start: 0, end: 4.5, text: "أنا <noun>راوية قصص</noun>. وأود أن <verb>أحكي</verb> لكم بعض القصص <adjective>الشخصية</adjective> حول ما أحب أن أسميه 'خطر القصة الواحدة'." },
            { start: 4.5, end: 9.8, text: "<verb>نشأت</verb> في <noun>حرم جامعي</noun> في <adjective>شرق</adjective> نيجيريا." },
            { start: 9.8, end: 15.2, text: "<verb>تقول</verb> <noun>أمي</noun> إنني <verb>بدأت</verb> <noun>القراءة</noun> في <noun>سن</noun> الثانية، على الرغم من أنني <verb>أعتقد</verb> أن سن الرابعة ربما <adjective>أقرب</adjective> إلى الحقيقة." },
          ],
          ko: [
            { start: 0, end: 4.5, text: "저는 <noun>이야기꾼</noun>입니다. 그리고 저는 여러분에게 제가 '단일 이야기의 위험'이라고 부르는 것에 대한 몇 가지 <adjective>개인적인</adjective> 이야기를 <verb>하고</verb> 싶습니다." },
            { start: 4.5, end: 9.8, text: "저는 나이지리아 <adjective>동부</adjective>의 <noun>대학 캠퍼스</noun>에서 <verb>자랐습니다</verb>." },
            { start: 9.8, end: 15.2, text: "<noun>어머니</noun>는 제가 두 살 때부터 <verb>읽기</verb> <verb>시작했다고</verb> <verb>말씀하시지만</verb>, 네 살이 진실에 더 <adjective>가깝다고</adjective> <verb>생각합니다</verb>." },
          ],
          fa: [
            { start: 0, end: 4.5, text: "من یک <noun>قصه‌گو</noun> هستم. و می‌خواهم چند داستان <adjective>شخصی</adjective> برای شما <verb>تعریف کنم</verb> درباره آنچه من 'خطر داستان واحد' می‌نامم." },
            { start: 4.5, end: 9.8, text: "من در یک <noun>محوطه دانشگاهی</noun> در <adjective>شرق</adjective> نیجریه <verb>بزرگ شدم</verb>." },
            { start: 9.8, end: 15.2, text: "<noun>مادرم</noun> <verb>می‌گوید</verb> که من در <noun>سن</noun> دو سالگی <verb>شروع به خواندن</verb> کردم، اگرچه <verb>فکر می‌کنم</verb> چهار سالگی احتمالاً به حقیقت <adjective>نزدیک‌تر</adjective> است." },
          ],
          zh: [
            { start: 0, end: 4.5, text: "我是一名<noun>讲故事的人</noun>。我想<verb>告诉</verb>你们一些<adjective>个人</adjective>故事，关于我喜欢称之为'单一故事的危险'。" },
            { start: 4.5, end: 9.8, text: "我在尼日利亚<adjective>东部</adjective>的一个<noun>大学校园</noun>里<verb>长大</verb>。" },
            { start: 9.8, end: 15.2, text: "我<noun>母亲</noun><verb>说</verb>我在两岁的<noun>时候</noun>就<verb>开始</verb><verb>阅读</verb>，尽管我<verb>认为</verb>四岁可能更<adjective>接近</adjective>事实。" },
          ],
          hi: [
            { start: 0, end: 4.5, text: "मैं एक <noun>कहानीकार</noun> हूँ। और मैं आपको कुछ <adjective>व्यक्तिगत</adjective> कहानियाँ <verb>सुनाना</verb> चाहती हूँ जिसे मैं 'एकल कहानी का खतरा' कहती हूँ।" },
            { start: 4.5, end: 9.8, text: "मैं नाइजीरिया के <adjective>पूर्वी</adjective> भाग में एक <noun>विश्वविद्यालय परिसर</noun> में <verb>बड़ी हुई</verb>।" },
            { start: 9.8, end: 15.2, text: "मेरी <noun>माँ</noun> <verb>कहती हैं</verb> कि मैंने दो साल की <noun>उम्र</noun> में <verb>पढ़ना</verb> <verb>शुरू</verb> कर दिया था, हालांकि मुझे <verb>लगता है</verb> कि चार साल सत्य के <adjective>करीब</adjective> है।" },
          ],
          pt: [
            { start: 0, end: 4.5, text: "Eu sou uma <noun>contadora de histórias</noun>. E eu gostaria de <verb>contar</verb> algumas histórias <adjective>pessoais</adjective> sobre o que eu gosto de chamar de 'o perigo da história única'." },
            { start: 4.5, end: 9.8, text: "Eu <verb>cresci</verb> em um <noun>campus universitário</noun> no <adjective>leste</adjective> da Nigéria." },
            { start: 9.8, end: 15.2, text: "Minha <noun>mãe</noun> <verb>diz</verb> que eu <verb>comecei</verb> a <verb>ler</verb> com a <noun>idade</noun> de dois anos, embora eu <verb>ache</verb> que quatro anos provavelmente está mais <adjective>próximo</adjective> da verdade." },
          ]
        };

        setCaptions(sampleCaptions);
        setAvailableLanguages(Object.keys(sampleCaptions));
        // Set English as primary language by default
        setSelectedLanguages({
          primary: 'en',
          secondary: '',
          tertiary: ''
        });
        setStatus(`Found human-created captions in ${Object.keys(sampleCaptions).length} languages!`);
      }
    }, 1000);
  };

  // Set language rank (primary, secondary, tertiary)
  const setLanguageRank = (lang, rank) => {
    // If language is already selected in another rank, clear it
    const updatedLanguages = { ...selectedLanguages };

    // If this language is already in another position, remove it
    Object.keys(updatedLanguages).forEach(key => {
      if (updatedLanguages[key] === lang && key !== rank) {
        updatedLanguages[key] = '';
      }
    });

    // Set the language to the new rank
    updatedLanguages[rank] = lang;
    setSelectedLanguages(updatedLanguages);
  };

  // Language names for display
  const languageNames = {
    en: 'English',
    de: 'German',
    fr: 'French',
    es: 'Spanish',
    ru: 'Russian',
    ar: 'Arabic',
    ko: 'Korean',
    fa: 'Persian',
    zh: 'Chinese',
    hi: 'Hindi',
    pt: 'Portuguese'
  };

  // Get color based on settings
  const getColor = (type) => {
    const colorMap = {
      'Electric Blue': '#007bff',
      'Cherry Red': '#dc3545',
      'Forest Green': '#28a745',
      'Sunny Yellow': '#ffc107',
      'Royal Purple': '#6f42c1'
    };

    switch(type) {
      case 'noun':
        return colorMap[nounColor] || colorMap['Electric Blue'];
      case 'verb':
        return colorMap[verbColor] || colorMap['Cherry Red'];
      case 'adjective':
        return colorMap[adjectiveColor] || colorMap['Forest Green'];
      default:
        return '#000000';
    }
  };

  // Get font size based on settings
  const getFontSize = () => {
    const sizeMap = {
      'Small': '0.875rem',
      'Medium': '1rem',
      'Large': '1.25rem',
      'X-Large': '1.5rem'
    };

    return sizeMap[fontSize] || '1rem';
  };

  // Get font family based on settings
  const getFontFamily = () => {
    const fontMap = {
      'Sans Serif': 'Arial, Helvetica, sans-serif',
      'Serif': 'Georgia, Times, serif',
      'Monospace': 'Courier New, monospace',
      'Handwriting': 'Comic Sans MS, cursive'
    };

    return fontMap[fontFamily] || 'Arial, Helvetica, sans-serif';
  };

  // Format caption text with styling for parts of speech
  const formatCaptionText = (text) => {
    // Replace tagged parts of speech with styled spans
    const formattedText = text
      .replace(/<noun>(.*?)<\/noun>/g, (match, content) => 
        `<span style="color:${getColor('noun')}; text-decoration:underline;">${content}</span>`)
      .replace(/<verb>(.*?)<\/verb>/g, (match, content) => 
        `<span style="color:${getColor('verb')}; text-decoration:underline;">${content}</span>`)
      .replace(/<adjective>(.*?)<\/adjective>/g, (match, content) => 
        `<span style="color:${getColor('adjective')}; text-decoration:underline;">${content}</span>`);

    return <span dangerouslySetInnerHTML={{ __html: formattedText }} />;
  };

  // Get selected languages as an array (for compatibility with the rest of the code)
  const getSelectedLanguagesArray = () => {
    return Object.values(selectedLanguages).filter(lang => lang !== '');
  };

  return (
    <div className="app-container">
      <h1 className="app-title">YouTube Multilingual Captions Extractor</h1>

      {/* Input Box */}
      <form onSubmit={handleSubmit} className="form-container">
        <div className="input-group">
          <input
            type="text"
            value={youtubeLink}
            onChange={handleLinkChange}
            placeholder="Paste YouTube URL here (e.g., https://www.youtube.com/watch?v=D9Ihs241zeg)"
            className="input-field"
          />
          <button 
            type="submit"
            className="submit-button"
          >
            Load Video
          </button>
        </div>
      </form>

      {/* Status message */}
      {status && (
        <div className="status-message">
          {status}
        </div>
      )}

      {videoId && (
        <div className="main-content">
          {/* Main Content Area (Video + Captions) */}
          <div className="content-area">
            {/* Video Embed Box */}
            <div className="video-container">
              <h2 className="section-title">Video</h2>
              <div className="video-wrapper">
                <iframe
                  className="video-frame"
                  src={`https://www.youtube.com/embed/${videoId}`}
                  title="YouTube video player"
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                ></iframe>
              </div>
            </div>

            {/* Captions Display */}
            {availableLanguages.length > 0 && (
              <div className="captions-container">
                <h2 className="section-title">Captions</h2>

                {getSelectedLanguagesArray().map(lang => (
                  <div key={lang} className="language-section">
                    <h3 className="language-title">
                      {languageNames[lang]} 
                      {lang === selectedLanguages.primary ? ' (Primary)' : 
                       lang === selectedLanguages.secondary ? ' (Secondary)' : 
                       lang === selectedLanguages.tertiary ? ' (Tertiary)' : ''}
                    </h3>
                    <div 
                      className="captions-content"
                      style={{
                        fontFamily: getFontFamily(),
                        fontSize: getFontSize(),
                        direction: ['ar', 'fa'].includes(lang) ? 'rtl' : 'ltr'
                      }}
                    >
                      {captions[lang]?.map((caption, index) => (
                        <div key={index} className="caption-item">
                          <div className="timestamp" style={{ direction: 'ltr' }}>
                            {Math.floor(caption.start / 60)}:{(caption.start % 60).toFixed(1).padStart(4, '0')} - 
                            {Math.floor(caption.end / 60)}:{(caption.end % 60).toFixed(1).padStart(4, '0')}
                          </div>
                          <div className="caption-text">
                            {formatCaptionText(caption.text)}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Customization Panel */}
          <div className="settings-panel">
            <h2 className="section-title">Customize Display</h2>

            {/* Language Selection - Updated with ranked dropdowns */}
            <div className="settings-section">
              <h3 className="settings-title">Select Languages</h3>

              {/* Primary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label">Primary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    value={selectedLanguages.primary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'primary')}
                    className="settings-select language-dropdown"
                  >
                    {availableLanguages.map(lang => (
                      <option key={lang} value={lang}>
                        {languageNames[lang]}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Secondary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label">Secondary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    value={selectedLanguages.secondary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'secondary')}
                    className="settings-select language-dropdown"
                  >
                    <option value="">None</option>
                    {availableLanguages.map(lang => (
                      <option key={lang} value={lang}>
                        {languageNames[lang]}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Tertiary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label">Tertiary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    value={selectedLanguages.tertiary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'tertiary')}
                    className="settings-select language-dropdown"
                  >
                    <option value="">None</option>
                    {availableLanguages.map(lang => (
                      <option key={lang} value={lang}>
                        {languageNames[lang]}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>

            {/* Caption Placement */}
            <div className="settings-item">
              <label className="settings-label">Caption Placement</label>
              <select 
                value={captionPlacement} 
                onChange={(e) => setCaptionPlacement(e.target.value)}
                className="settings-select"
              >
                <option>Below Video</option>
                <option>Overlay Bottom</option>
                <option>Side Panel</option>
              </select>
            </div>

            {/* Noun Underline Color */}
            <div className="settings-item">
              <label className="settings-label">Noun Underline Color</label>
              <select 
                value={nounColor} 
                onChange={(e) => setNounColor(e.target.value)}
                className="settings-select"
              >
                <option>Electric Blue</option>
                <option>Cherry Red</option>
                <option>Forest Green</option>
                <option>Sunny Yellow</option>
                <option>Royal Purple</option>
              </select>
            </div>

            {/* Verb Underline Color */}
            <div className="settings-item">
              <label className="settings-label">Verb Underline Color</label>
              <select 
                value={verbColor} 
                onChange={(e) => setVerbColor(e.target.value)}
                className="settings-select"
              >
                <option>Electric Blue</option>
                <option>Cherry Red</option>
                <option>Forest Green</option>
                <option>Sunny Yellow</option>
                <option>Royal Purple</option>
              </select>
            </div>

            {/* Adjective Underline Color */}
            <div className="settings-item">
              <label className="settings-label">Adjective Underline Color</label>
              <select 
                value={adjectiveColor} 
                onChange={(e) => setAdjectiveColor(e.target.value)}
                className="settings-select"
              >
                <option>Electric Blue</option>
                <option>Cherry Red</option>
                <option>Forest Green</option>
                <option>Sunny Yellow</option>
                <option>Royal Purple</option>
              </select>
            </div>

            {/* Font Size */}
            <div className="settings-item">
              <label className="settings-label">Font Size</label>
              <select 
                value={fontSize} 
                onChange={(e) => setFontSize(e.target.value)}
                className="settings-select"
              >
                <option>Small</option>
                <option>Medium</option>
                <option>Large</option>
                <option>X-Large</option>
              </select>
            </div>

            {/* Font Family */}
            <div className="settings-item">
              <label className="settings-label">Font Family</label>
              <select 
                value={fontFamily} 
                onChange={(e) => setFontFamily(e.target.value)}
                className="settings-select"
              >
                <option>Sans Serif</option>
                <option>Serif</option>
                <option>Monospace</option>
                <option>Handwriting</option>
              </select>
            </div>

            {/* Export button */}
            <div className="export-section">
              <button 
                className="export-button"
                onClick={() => {
                  const captionsData = JSON.stringify(captions, null, 2);
                  const blob = new Blob([captionsData], { type: 'application/json' });
                  const url = URL.createObjectURL(blob);
                  const a = document.createElement('a');
                  a.href = url;
                  a.download = `captions-${videoId}.json`;
                  a.click();
                }}
              >
                Export Captions as JSON
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
