  };

  // Helper function to get language name
  const getLanguageName = (langCode) => {
    const transcript = findTranscriptByLanguageCode(langCode);
    return transcript?.language || langCode;
  };

  // Start dragging the caption overlay
  const handleMouseDown = (e) => {
    if (captionPlacement !== 'Overlay') return;

    const videoContainer = videoContainerRef.current;
    if (!videoContainer) return;

    // Set dragging to true and store initial positions
    setOverlayPosition(prev => ({ 
      ...prev, 
      isDragging: true,
      startX: e.clientX,
      startY: e.clientY
    }));

    // Add event listeners
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);

    // Prevent default dragging behavior
    e.preventDefault();
  };

  // Handle mouse move during dragging - Completely redesigned for immediate, direct movement
  const handleMouseMove = (e) => {
    if (!overlayPosition.isDragging) return;

    // Get the video container dimensions
    const videoContainer = videoContainerRef.current;
    if (!videoContainer) return;

    const rect = videoContainer.getBoundingClientRect();

    // Calculate direct position as percentage of container
    // This immediately places the overlay where the mouse is instead of incremental movement
    const newX = Math.max(0, Math.min(100, ((e.clientX - rect.left) / rect.width) * 100));
    const newY = Math.max(0, Math.min(100, ((e.clientY - rect.top) / rect.height) * 100));

