                    } else if (e.key === 'ArrowDown') {
                      setOverlayPosition(prev => ({
                        ...prev,
                        y: Math.min(100, prev.y + step)
                      }));
                      e.preventDefault();
                    }
                  }}
                >
                  <div className="overlay-drag-instructions">
                    Hold and drag to reposition
                  </div>

                  {getSelectedLanguagesArray().map(lang => (
                    activeCaptions[lang] && (
                      <div key={lang} className="overlay-caption">
                        <span className="overlay-language">
                          {getLanguageName(lang)}:
                        </span>
                        <span 
                          className="overlay-text"
                          style={getCaptionStyle(lang)}
                        >
                          {formatCaptionText(activeCaptions[lang].text, lang)}
                        </span>
                      </div>
                    )
                  ))}
                </div>
              )}
            </div>

            {/* Captions Display - for Below Video mode only - with reduced spacing */}
            {captionPlacement === 'Below Video' && 
             availableLanguages.length > 0 && (
              <div className="captions-container closer-to-video">

                {/* Current active captions section - all languages together */}
                <div className={`active-captions-section ${captionsHeight === 'expanded' ? 'expanded' : ''}`}>
                  <button
                    className="toggle-caption-height"
                    onClick={toggleCaptionsHeight}
                    title={captionsHeight === 'normal' ? 'Expand captions' : 'Collapse captions'}
                  >
                    {captionsHeight === 'normal' ? '↓' : '↑'}
                  </button>

                  {getSelectedLanguagesArray().map(lang => {
                  // Skip hidden languages
                  if (hiddenLanguages[lang]) return null;

                  return (
                    <div 
                      key={`active-${lang}`} 
                      className="caption-text-container"
                    >
                      <span className={`language-tag ${lang === selectedLanguages.primary ? 'primary' : 'secondary'}`}>
                        {getLanguageName(lang)}
                      </span>

                      {activeCaptions[lang] ? (
                        <div className="caption-text" style={getCaptionStyle(lang)}>
                          {formatCaptionText(activeCaptions[lang].text, lang)}
                        </div>
                      ) : (
                        <span className="no-caption">No caption at current timestamp</span>
                      )}
                    </div>
                  );
                })}
                </div>

                {/* Caption resize handle */}
                <div 
                  ref={captionsResizeHandleRef}
                  className="resize-handle resize-handle-vertical" 
                  title="Drag to resize captions height"
                ></div>

                {/* Transcripts section - all languages */}
                <div className="transcripts-section">
                  <h3 className="section-subtitle">Full Transcripts</h3>

                  {getSelectedLanguagesArray().map(lang => (
                    <div key={`transcript-${lang}`} className="language-section">
                      <h4 className="language-title">
                        {getLanguageName(lang)} 
                        {lang === selectedLanguages.primary ? ' (Primary)' : 
                         lang === selectedLanguages.secondary ? ' (Secondary)' : ''}
                      </h4>

                      {/* Full transcript with timestamps */}
                      <div className="captions-content">
                        {captions[lang]?.map((caption, index) => (
                          <div 
                            key={index} 
                            className={`caption-item ${currentTime >= caption.start && currentTime <= caption.end ? 'active-caption' : ''}`}
                            onClick={() => jumpToTime(caption.start)}
                            tabIndex="0"
                            role="button"
                            aria-label={`Jump to ${Math.floor(caption.start / 60)}:${(caption.start % 60).toFixed(1).padStart(4, '0')}`}
                            onKeyDown={(e) => {
                              if (e.key === 'Enter' || e.key === ' ') {
                                jumpToTime(caption.start);
                              }
                            }}
                          >
                            <div className="timestamp">
                              {Math.floor(caption.start / 60)}:{(caption.start % 60).toFixed(1).padStart(4, '0')} - 
                              {Math.floor(caption.end / 60)}:{(caption.end % 60).toFixed(1).padStart(4, '0')}
                            </div>
                            <div className="caption-text" style={getCaptionStyle(lang)}>
                              {formatCaptionText(caption.text, lang)}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Settings Panel - Now with improved toggle */}
          <div className={`settings-panel ${settingsPanelCollapsed ? 'collapsed' : ''}`}>
            <button 
              className="settings-toggle" 
              onClick={toggleSettingsPanel}
              title={settingsPanelCollapsed ? "Expand settings panel" : "Collapse settings panel"}
            >
              <span className="settings-toggle-icon">
                {settingsPanelCollapsed ? '»' : '«'}
              </span>
            </button>

            <h2 className="section-title">Customize Display</h2>

            {/* Language Selection */}
            <div className="settings-section">
              <h3 className="settings-title">Select Languages</h3>

              {/* Primary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label" htmlFor="primary-language">Primary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    id="primary-language"
                    value={selectedLanguages.primary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'primary')}
                    className="settings-select language-dropdown"
                  >
                    {availableTranscripts.map(transcript => (
                      <option key={transcript.language_code} value={transcript.language_code}>
                        {transcript.language}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Secondary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label" htmlFor="secondary-language">Secondary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    id="secondary-language"
                    value={selectedLanguages.secondary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'secondary')}
                    className="settings-select language-dropdown"
                  >
                    <option value="">None</option>
                    {availableTranscripts.map(transcript => (
                      <option key={transcript.language_code} value={transcript.language_code}>
                        {transcript.language}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>

            {/* Language-specific customization */}
            <div className="settings-section">
              <h3 className="settings-title">Language Customizations</h3>

              {getSelectedLanguagesArray().map(lang => (
                <div key={`customize-${lang}`} className="language-customization-card">
                  <div className="language-card-header"
                    onClick={() => toggleLanguageCollapse(lang)}
                    style={{ cursor: 'pointer' }}
                  >
                    <div className="language-header-content">
                      <span className="language-name">{getLanguageName(lang)}</span>
                      {lang === selectedLanguages.primary && <span className="language-tag primary">Primary</span>}
                      {lang === selectedLanguages.secondary && <span className="language-tag secondary">Secondary</span>}
                    </div>
                    {/* Collapse/Expand icon */}
                    <span className="collapse-icon">
                      {collapsedLanguages[lang] ? '▶' : '▼'}
                    </span>
                  </div>

                  {!collapsedLanguages[lang] && (   
                  <div className="language-card-content">
                    {/* Font Type */}
                    <div className="settings-item">
                      <label className="settings-label">Visibility</label>
                      <button 
                        className={`visibility-toggle ${hiddenLanguages[lang] ? 'hidden' : 'visible'}`}
                        onClick={() => toggleLanguageVisibility(lang)}
                      >
                        {hiddenLanguages[lang] ? 'Show language' : 'Hide language'}
                      </button>
                      <label className="settings-label" htmlFor={`font-family-${lang}`}>Font</label>
                      <select 
                        id={`font-family-${lang}`}
                        value={languageCustomizations[lang]?.fontFamily || 'Arial / Helvetica'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'fontFamily', e.target.value)}
                        className="settings-select"
                      >
                        <optgroup label="Sans-serif">
                          <option>Arial / Helvetica</option>
                          <option>Roboto</option>
                          <option>Open Sans</option>
                          <option>Noto Sans</option>
                          <option>Verdana</option>
                        </optgroup>
                        <optgroup label="Monospace">
                          <option>Courier New</option>
                          <option>Consolas</option>
                          <option>Source Code Pro</option>
                        </optgroup>
                      </select>
                    </div>


                    {/* Font Size */}
                    <div className="settings-item">
                      <label className="settings-label" htmlFor={`font-size-${lang}`}>Size</label>
                      <select 
                        id={`font-size-${lang}`}
                        value={languageCustomizations[lang]?.fontSize || 'Large'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'fontSize', e.target.value)}
                        className="settings-select"
                      >
                        <option>Small</option>
                        <option>Medium</option>
                        <option>Large</option>
                        <option>X-Large</option>
                      </select>
                    </div>

                    {/* Font Weight */}
                    <div className="settings-item">
                      <label className="settings-label" htmlFor={`font-weight-${lang}`}>Font Weight</label>
                      <select 
                        id={`font-weight-${lang}`}
                        value={languageCustomizations[lang]?.fontWeight || 'bold'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'fontWeight', e.target.value)}
                        className="settings-select"
                      >
                        <option value="normal">Normal</option>
                        <option value="bold">Bold</option>
                        <option value="600">Semi-Bold</option>
                        <option value="300">Light</option>
                      </select>
                    </div>

                    {/* Text Color */}
                    <div className="settings-item">
                      <label className="settings-label" htmlFor={`text-color-${lang}`}>Color</label>
                      <select 
                        id={`text-color-${lang}`}
                        value={languageCustomizations[lang]?.textColor || '#000000'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'textColor', e.target.value)}
                        className="settings-select"
                      >
                        <option value="#000000">Black</option>
                        <option value="#FFFFFF">White</option>
                        <option value="#333333">Dark Gray</option>
                        <option value="#1A1AFF">Dark Blue</option>
                      </select>
                    </div>

                    {/* Character Spacing */}
                    <div className="settings-item">
                      <label className="settings-label" htmlFor={`letter-spacing-${lang}`}>Character Spacing</label>
                      <select 
                        id={`letter-spacing-${lang}`}
                        value={languageCustomizations[lang]?.letterSpacing || 'normal'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'letterSpacing', e.target.value)}
                        className="settings-select"
                      >
                        <option value="normal">Normal</option>
                        <option value="0.05em">Slightly Expanded</option>
                        <option value="0.1em">Expanded</option>
                        <option value="-0.03em">Condensed</option>
                      </select>
                    </div>

                    <button 
                      className="reset-button"
                      onClick={() => {
                        // Reset this language's customizations to new defaults with larger, bolder text
                        setLanguageCustomizations(prev => ({
                          ...prev,
                          [lang]: {
                            fontFamily: 'Arial / Helvetica',
                            fontSize: 'Large', // Updated from Medium to Large
                            fontWeight: 'bold', // New default
                            textColor: darkMode ? '#FFFFFF' : '#000000',
                            letterSpacing: 'normal'
                          }
                        }));
                      }}
                      aria-label={`Reset customization for ${getLanguageName(lang)}`}
                    >
                      Reset
                    </button>
                  </div>
                  )}
                </div>
              ))}
            </div>

            {/* Part of speech color customization */}
            <div className="settings-section">
              <h3 className="settings-title">Part of Speech Highlighting</h3>
              <div className="pos-color-controls">
                <div className="pos-color-item">
                  <label className="pos-label" htmlFor="noun-color">Nouns</label>
                  <select 
                    id="noun-color" 
                    value={nounColor} 
                    onChange={(e) => setNounColor(e.target.value)} 
                    className="settings-select pos-select"
                  >
                    <option>Electric Blue</option>
                    <option>Cherry Red</option>
                    <option>Forest Green</option>
                    <option>Sunny Yellow</option>
                    <option>Royal Purple</option>
                  </select>
                </div>

                <div className="pos-color-item">
                  <label className="pos-label" htmlFor="verb-color">Verbs</label>
                  <select 
                    id="verb-color" 
                    value={verbColor} 
                    onChange={(e) => setVerbColor(e.target.value)} 
                    className="settings-select pos-select"
                  >
                    <option>Electric Blue</option>
                    <option>Cherry Red</option>
                    <option>Forest Green</option>
                    <option>Sunny Yellow</option>
                    <option>Royal Purple</option>
                  </select>
                </div>

                <div className="pos-color-item">
                  <label className="pos-label" htmlFor="adjective-color">Adjectives</label>
                  <select 
                    id="adjective-color" 
                    value={adjectiveColor} 
                    onChange={(e) => setAdjectiveColor(e.target.value)} 
                    className="settings-select pos-select"
                  >
                    <option>Electric Blue</option>
                    <option>Cherry Red</option>
                    <option>Forest Green</option>
                    <option>Sunny Yellow</option>
                    <option>Royal Purple</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
