import React, { useState, useEffect, useRef } from 'react';
import './App.css';

// API base URL - change this if your backend is running on a different port/host
const API_BASE_URL = 'http://127.0.0.1:5000';

function App() {
  const [youtubeLink, setYoutubeLink] = useState('https://www.youtube.com/watch?v=D9Ihs241zeg');
  const [videoId, setVideoId] = useState('');
  const [captions, setCaptions] = useState({});
  const [selectedLanguages, setSelectedLanguages] = useState({
    primary: 'en',
    secondary: '',
    tertiary: ''
  });
  const [availableLanguages, setAvailableLanguages] = useState([]);
  const [availableTranscripts, setAvailableTranscripts] = useState([]);
  const [status, setStatus] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [activeCaptions, setActiveCaptions] = useState({});

  // Caption placement state with updated options
  const [captionPlacement, setCaptionPlacement] = useState('Below Video');

  // New state for overlay position
  const [overlayPosition, setOverlayPosition] = useState({
    vertical: 'bottom',
    horizontal: 'center'
  });

  // Global customization options
  const [nounColor, setNounColor] = useState('Electric Blue');
  const [verbColor, setVerbColor] = useState('Cherry Red');
  const [adjectiveColor, setAdjectiveColor] = useState('Forest Green');

  // Per-language customization settings (simplified)
  const [languageCustomizations, setLanguageCustomizations] = useState({});
  const [highContrastMode, setHighContrastMode] = useState(false);

  // Effect to set high contrast mode when overlay is selected
  useEffect(() => {
    if (captionPlacement === 'Overlay') {
      setHighContrastMode(true);
    }
  }, [captionPlacement]);

  // Extract video ID from YouTube URL
  const extractVideoId = (url) => {
    const regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#&?]*).*/;
    const match = url.match(regExp);
    return (match && match[7].length === 11) ? match[7] : null;
  };

  // Initialize with the default URL
  useEffect(() => {
    const id = extractVideoId(youtubeLink);
    if (id) {
      setVideoId(id);
    }
  }, []);

  // Initialize language customizations when new languages are selected
  useEffect(() => {
    const newLanguageCustomizations = { ...languageCustomizations };

    // Initialize customizations for any new languages with simplified options
    Object.values(selectedLanguages).forEach(lang => {
      if (lang && !newLanguageCustomizations[lang]) {
        newLanguageCustomizations[lang] = {
          fontFamily: 'Arial / Helvetica',
          fontSize: 'Medium',
          textColor: '#000000',
          letterSpacing: 'normal'
        };
      }
    });

    setLanguageCustomizations(newLanguageCustomizations);
  }, [selectedLanguages]);

  // Handle YouTube link input
  const handleLinkChange = (e) => {
    setYoutubeLink(e.target.value);
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const id = extractVideoId(youtubeLink);
    if (id) {
      setVideoId(id);
      setStatus('Fetching available languages...');
      setIsLoading(true);
      fetchAvailableLanguages(id);
    } else {
      setStatus('Invalid YouTube URL');
    }
  };

  // Function to fetch available languages using the youtube_transcript_api
  const fetchAvailableLanguages = async (videoId) => {
    try {
      console.log(`Fetching languages for video ID: ${videoId}`);
      const response = await fetch(`${API_BASE_URL}/api/list-transcripts?videoId=${videoId}`);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`API error (${response.status}):`, errorText);
        throw new Error(`Failed to fetch transcript list: ${response.status}`);
      }

      const data = await response.json();
      console.log('Received data:', data);

      if (data.error) {
        console.error('API returned error:', data.error);
        throw new Error(data.error);
      }

      // Store the full transcript information
      setAvailableTranscripts(data.transcripts);

      // Also keep the language codes for backward compatibility
      const languageCodes = data.transcripts.map(t => t.language_code);
      setAvailableLanguages(languageCodes);
      console.log(`Found ${languageCodes.length} languages:`, languageCodes);

      // Set primary language to English if available, otherwise first available language
      const defaultPrimary = data.transcripts.find(t => t.language_code === 'en')?.language_code 
        || (data.transcripts[0]?.language_code || '');

      setSelectedLanguages({
        primary: defaultPrimary,
        secondary: '',
        tertiary: ''
      });

      setStatus(`Found captions in ${languageCodes.length} languages. Fetching transcripts...`);

      // Fetch transcripts for all available languages
      await fetchCaptions(videoId, languageCodes);

    } catch (error) {
      console.error('Error fetching transcript languages:', error);
      setStatus(`Error: ${error.message}`);
      setIsLoading(false);
    }
  };

  // Function to fetch transcripts for all available languages
  const fetchCaptions = async (videoId, languages) => {
    try {
      const captionsData = {};

      // For each language, fetch the transcript
      for (const lang of languages) {
        try {
          const response = await fetch(`${API_BASE_URL}/api/get-transcript?videoId=${videoId}&lang=${lang}`);

          if (!response.ok) {
            console.warn(`Failed to fetch transcript for ${lang}: ${response.status}`);
            continue;
          }

          const data = await response.json();

          if (data.error) {
            console.warn(`Error fetching transcript for ${lang}: ${data.error}`);
            continue;
          }

          // Process the transcript data - add POS tagging
          const processedTranscript = data.transcript.map(caption => ({
            ...caption,
            text: applyPOSTagging(caption.text),
            end: caption.start + caption.duration // Calculate end time
          }));

          captionsData[lang] = processedTranscript;
        } catch (error) {
          console.warn(`Error processing transcript for ${lang}:`, error);
        }
      }

      setCaptions(captionsData);
      setStatus(`Successfully loaded captions in ${Object.keys(captionsData).length} languages`);
    } catch (error) {
      console.error('Error fetching captions:', error);
      setStatus(`Error fetching captions: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Function to apply part-of-speech tagging (simplified for demo)
  const applyPOSTagging = (text) => {
    // This is a simplified implementation. In a production environment,
    // you would use a more sophisticated NLP approach.

    // Common words lists for basic POS tagging
    const commonNouns = ['person', 'time', 'year', 'way', 'day', 'thing', 'man', 'world', 'life', 'hand', 'part', 'child', 'eye', 'woman', 'place', 'work', 'week', 'case', 'point', 'government', 'company', 'number', 'group', 'problem', 'fact'];
    const commonVerbs = ['be', 'have', 'do', 'say', 'go', 'can', 'get', 'would', 'make', 'know', 'will', 'think', 'take', 'see', 'come', 'could', 'want', 'look', 'use', 'find', 'give', 'tell', 'work', 'may', 'should', 'call', 'try', 'ask', 'need', 'feel', 'become', 'leave', 'put', 'mean', 'keep', 'let', 'begin', 'seem', 'help', 'talk', 'turn', 'start', 'might', 'show', 'hear', 'play', 'run', 'move', 'like', 'live', 'believe', 'hold', 'bring', 'happen', 'must', 'write', 'provide'];
    const commonAdjectives = ['good', 'new', 'first', 'last', 'long', 'great', 'little', 'own', 'other', 'old', 'right', 'big', 'high', 'different', 'small', 'large', 'next', 'early', 'young', 'important', 'few', 'public', 'bad', 'same', 'able', 'true'];

    // Tokenize the text
    const words = text.split(/\s+/);
    let taggedText = '';

    for (let i = 0; i < words.length; i++) {
      const originalWord = words[i];
      const word = originalWord.toLowerCase().replace(/[^\w\s]/g, '');

      if (commonNouns.includes(word)) {
        taggedText += originalWord.replace(word, `<noun>${word}</noun>`);
      } else if (commonVerbs.includes(word)) {
        taggedText += originalWord.replace(word, `<verb>${word}</verb>`);
      } else if (commonAdjectives.includes(word)) {
        taggedText += originalWord.replace(word, `<adjective>${word}</adjective>`);
      } else {
        taggedText += originalWord;
      }

      if (i < words.length - 1) {
        taggedText += ' ';
      }
    }

    return taggedText;
  };

  // Set language rank (primary, secondary, tertiary)
  const setLanguageRank = (lang, rank) => {
    // If language is already selected in another rank, clear it
    const updatedLanguages = { ...selectedLanguages };

    // If this language is already in another position, remove it
    Object.keys(updatedLanguages).forEach(key => {
      if (updatedLanguages[key] === lang && key !== rank) {
        updatedLanguages[key] = '';
      }
    });

    // Set the language to the new rank
    updatedLanguages[rank] = lang;
    setSelectedLanguages(updatedLanguages);
  };

  // Update customization for a specific language
  const updateLanguageCustomization = (lang, property, value) => {
    setLanguageCustomizations(prev => ({
      ...prev,
      [lang]: {
        ...prev[lang],
        [property]: value
      }
    }));
  };

  // Get color based on settings
  const getColor = (type) => {
    const colorMap = {
      'Electric Blue': '#007bff',
      'Cherry Red': '#dc3545',
      'Forest Green': '#28a745',
      'Sunny Yellow': '#ffc107',
      'Royal Purple': '#6f42c1'
    };

    switch(type) {
      case 'noun':
        return colorMap[nounColor] || colorMap['Electric Blue'];
      case 'verb':
        return colorMap[verbColor] || colorMap['Cherry Red'];
      case 'adjective':
        return colorMap[adjectiveColor] || colorMap['Forest Green'];
      default:
        return '#000000';
    }
  };

  // Get font size based on settings
  const getFontSize = (sizeOption) => {
    const sizeMap = {
      'Small': '0.875rem',
      'Medium': '1rem',
      'Large': '1.25rem',
      'X-Large': '1.5rem'
    };

    return sizeMap[sizeOption] || '1rem';
  };

  // Get font family based on settings
  const getFontFamily = (fontOption) => {
    const fontMap = {
      'Arial / Helvetica': 'Arial, Helvetica, sans-serif',
      'Roboto': 'Roboto, Arial, sans-serif',
      'Open Sans': 'Open Sans, Arial, sans-serif',
      'Noto Sans': 'Noto Sans, Arial, sans-serif',
      'Verdana': 'Verdana, Geneva, sans-serif',
      'Courier New': 'Courier New, monospace',
      'Consolas': 'Consolas, monospace',
      'Source Code Pro': 'Source Code Pro, monospace',
      'Sans Serif': 'Arial, Helvetica, sans-serif',
      'Serif': 'Georgia, Times, serif',
      'Monospace': 'Courier New, monospace',
      'Handwriting': 'Comic Sans MS, cursive'
    };

    return fontMap[fontOption] || 'Arial, Helvetica, sans-serif';
  };

  // Format caption text with styling for parts of speech (simplified)
  const formatCaptionText = (text, lang) => {
    if (!text) return '';

    const customization = languageCustomizations[lang] || {
      fontFamily: 'Arial / Helvetica',
      fontSize: 'Medium',
      textColor: '#000000',
      letterSpacing: 'normal'
    };

    // Define base style for the caption (simplified)
    let baseStyle = `
      color: ${highContrastMode ? '#FFFFFF' : customization.textColor};
      font-family: ${getFontFamily(customization.fontFamily)};
      font-size: ${getFontSize(customization.fontSize)};
      letter-spacing: ${customization.letterSpacing};
      display: inline-block;
    `;

    // Replace tagged parts of speech with styled spans
    const formattedText = text
      .replace(/<noun>(.*?)<\/noun>/g, (match, content) => 
        `<span style="${baseStyle}; color:${getColor('noun')}; text-decoration:underline;">${content}</span>`)
      .replace(/<verb>(.*?)<\/verb>/g, (match, content) => 
        `<span style="${baseStyle}; color:${getColor('verb')}; text-decoration:underline;">${content}</span>`)
      .replace(/<adjective>(.*?)<\/adjective>/g, (match, content) => 
        `<span style="${baseStyle}; color:${getColor('adjective')}; text-decoration:underline;">${content}</span>`);

    return <span dangerouslySetInnerHTML={{ __html: formattedText }} />;
  };

  // Get selected languages as an array (for compatibility with the rest of the code)
  const getSelectedLanguagesArray = () => {
    return Object.values(selectedLanguages).filter(lang => lang !== '');
  };

  // Get caption style for a specific language (simplified)
  const getCaptionStyle = (lang) => {
    const customization = languageCustomizations[lang] || {};
    const isRTL = ['ar', 'fa', 'he', 'ur'].includes(lang);

    return {
      fontFamily: getFontFamily(customization.fontFamily),
      fontSize: getFontSize(customization.fontSize),
      color: highContrastMode ? '#FFFFFF' : (customization.textColor || '#000000'),
      letterSpacing: customization.letterSpacing || 'normal',
      backgroundColor: highContrastMode ? '#000000' : 'transparent',
      direction: isRTL ? 'rtl' : 'ltr'
    };
  };

  // Update the active captions based on current time
  useEffect(() => {
    const newActiveCaptions = {};

    getSelectedLanguagesArray().forEach(lang => {
      if (captions[lang]) {
        const activeCaption = captions[lang].find(
          caption => currentTime >= caption.start && currentTime <= caption.end
        );

        if (activeCaption) {
          newActiveCaptions[lang] = activeCaption;
        }
      }
    });

    setActiveCaptions(newActiveCaptions);
  }, [currentTime, captions, selectedLanguages]);

  // Set up a timer to update the current time regularly
  useEffect(() => {
    if (!videoId) return;

    const interval = setInterval(() => {
      // If we have access to the YouTube iframe, get the current time
      if (window.YT && window.YT.Player && window.player) {
        try {
          const currentTime = window.player.getCurrentTime();
          if (currentTime) {
            setCurrentTime(currentTime);
          }
        } catch (error) {
          console.error('Error getting video time:', error);
        }
      }
    }, 500); // Check every 500ms

    return () => clearInterval(interval);
  }, [videoId]);

  // Initialize YouTube API and player
  useEffect(() => {
    if (!videoId) return;

    // Load YouTube iframe API if it's not already loaded
    if (!window.YT) {
      const tag = document.createElement('script');
      tag.src = 'https://www.youtube.com/iframe_api';
      const firstScriptTag = document.getElementsByTagName('script')[0];
      firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

      window.onYouTubeIframeAPIReady = () => {
        createYouTubePlayer();
      };
    } else {
      createYouTubePlayer();
    }

    function createYouTubePlayer() {
      if (window.player) {
        window.player.destroy();
      }

      window.player = new window.YT.Player('youtube-player', {
        videoId: videoId,
        events: {
          'onReady': onPlayerReady,
          'onStateChange': onPlayerStateChange
        }
      });
    }

    function onPlayerReady(event) {
      console.log('YouTube player ready');
    }

    function onPlayerStateChange(event) {
      // Update current time when video is playing
      if (event.data === window.YT.PlayerState.PLAYING) {
        const updateTime = () => {
          if (window.player && window.player.getCurrentTime) {
            setCurrentTime(window.player.getCurrentTime());
          }
          if (window.player && window.player.getPlayerState() === window.YT.PlayerState.PLAYING) {
            requestAnimationFrame(updateTime);
          }
        };
        updateTime();
      }
    }

    return () => {
      if (window.player) {
        window.player.destroy();
        window.player = null;
      }
    };
  }, [videoId]);

  // Find transcript by language code
  const findTranscriptByLanguageCode = (code) => {
    return availableTranscripts.find(t => t.language_code === code);
  };

  // Helper function to get language name
  const getLanguageName = (langCode) => {
    const transcript = findTranscriptByLanguageCode(langCode);
    return transcript?.language || langCode;
  };

  return (
    <div className="app-container">
      <h1 className="app-title">YouTube Multilingual Captions Extractor</h1>

      {/* Input Box */}
      <form onSubmit={handleSubmit} className="form-container">
        <div className="input-group">
          <input
            type="text"
            value={youtubeLink}
            onChange={handleLinkChange}
            placeholder="Paste YouTube URL here (e.g., https://www.youtube.com/watch?v=D9Ihs241zeg)"
            className="input-field"
          />
          <button 
            type="submit"
            className="submit-button"
            disabled={isLoading}
          >
            {isLoading ? 'Loading...' : 'Load Video'}
          </button>
        </div>
      </form>

      {/* Status message */}
      {status && (
        <div className="status-message">
          {status}
        </div>
      )}

      {videoId && (
        <div className="main-content">
          {/* Main Content Area (Video + Captions) */}
          <div className="content-area">
            {/* Video Embed Box */}
            <div className="video-container">
              <h2 className="section-title">Video</h2>
              <div className="video-wrapper">
                {/* Changed to use YouTube API for better time synchronization */}
                <div id="youtube-player" className="video-frame"></div>
              </div>

              {/* Real-time captions overlay - redesigned with positioning */}
              {captionPlacement === 'Overlay' && (
                <div 
                  className={`captions-overlay ${overlayPosition.vertical} ${overlayPosition.horizontal}`}
                  style={{
                    backgroundColor: 'rgba(0, 0, 0, 0.7)'
                  }}
                >
                  <button 
                    className="move-overlay-button" 
                    title="Move captions"
                    onClick={() => {
                      // Cycle through positions
                      let newPosition = {...overlayPosition};

                      // Cycle through vertical positions
                      if (overlayPosition.vertical === 'bottom') {
                        newPosition.vertical = 'top';
                      } else if (overlayPosition.vertical === 'top') {
                        newPosition.vertical = 'center';
                      } else {
                        newPosition.vertical = 'bottom';
                      }

                      // If we've cycled through all vertical positions, change horizontal
                      if (newPosition.vertical === 'bottom') {
                        if (overlayPosition.horizontal === 'center') {
                          newPosition.horizontal = 'left';
                        } else if (overlayPosition.horizontal === 'left') {
                          newPosition.horizontal = 'right';
                        } else {
                          newPosition.horizontal = 'center';
                        }
                      }

                      setOverlayPosition(newPosition);
                    }}
                  >

                  </button>

                  {getSelectedLanguagesArray().map(lang => (
                    activeCaptions[lang] && (
                      <div key={lang} className="overlay-caption">
                        <span className="overlay-language">
                          {getLanguageName(lang)}:
                        </span>
                        <span 
                          className="overlay-text"
                          style={getCaptionStyle(lang)}
                        >
                          {formatCaptionText(activeCaptions[lang].text, lang)}
                        </span>
                      </div>
                    )
                  ))}
                </div>
              )}
            </div>

            {/* Captions Display - for Below Video mode only */}
            {captionPlacement === 'Below Video' && 
             availableLanguages.length > 0 && (
              <div className="captions-container">

                {/* Current active captions section - all languages together */}
                <div className="active-captions-section">

                  {getSelectedLanguagesArray().map(lang => (
                    <div 
                      key={`active-${lang}`} 
                      className="active-caption-display"
                      style={{
                        padding: '10px',
                        marginBottom: '15px',
                        backgroundColor: '#f0f8ff',
                        border: '1px solid #007bff',
                        borderRadius: '5px'
                      }}
                    >
                      <h4 className="language-title" style={{ margin: '0 0 5px 0' }}>
                        {getLanguageName(lang)} 
                        {lang === selectedLanguages.primary ? ' (Primary)' : 
                         lang === selectedLanguages.secondary ? ' (Secondary)' : 
                         lang === selectedLanguages.tertiary ? ' (Tertiary)' : ''}
                      </h4>

                      {activeCaptions[lang] ? (
                        <>
                          <div className="timestamp" style={{ direction: 'ltr', fontWeight: 'bold' }}>
                            {Math.floor(activeCaptions[lang].start / 60)}:
                            {(activeCaptions[lang].start % 60).toFixed(1).padStart(4, '0')} - 
                            {Math.floor(activeCaptions[lang].end / 60)}:
                            {(activeCaptions[lang].end % 60).toFixed(1).padStart(4, '0')}
                          </div>
                          <div className="caption-text" style={getCaptionStyle(lang)}>
                            {formatCaptionText(activeCaptions[lang].text, lang)}
                          </div>
                        </>
                      ) : (
                        <span className="no-caption">No caption at current timestamp</span>
                      )}
                    </div>
                  ))}
                </div>

                {/* Transcripts section - all languages */}
                <div className="transcripts-section">
                  <h3 className="section-subtitle">Full Transcripts</h3>

                  {getSelectedLanguagesArray().map(lang => (
                    <div key={`transcript-${lang}`} className="language-section">
                      <h4 className="language-title">
                        {getLanguageName(lang)} 
                        {lang === selectedLanguages.primary ? ' (Primary)' : 
                         lang === selectedLanguages.secondary ? ' (Secondary)' : 
                         lang === selectedLanguages.tertiary ? ' (Tertiary)' : ''}
                      </h4>

                      {/* Full transcript */}
                      <div 
                        className="captions-content"
                        style={{
                          ...getCaptionStyle(lang),
                          maxHeight: '300px',
                          overflowY: 'auto',
                          marginBottom: '20px'
                        }}
                      >
                        {captions[lang]?.map((caption, index) => (
                          <div 
                            key={index} 
                            className={`caption-item ${currentTime >= caption.start && currentTime <= caption.end ? 'active-caption' : ''}`}
                            onClick={() => {
                              if (window.player && window.player.seekTo) {
                                window.player.seekTo(caption.start);
                              }
                            }}
                            style={{ cursor: 'pointer' }}
                          >
                            <div className="timestamp" style={{ direction: 'ltr' }}>
                              {Math.floor(caption.start / 60)}:{(caption.start % 60).toFixed(1).padStart(4, '0')} - 
                              {Math.floor(caption.end / 60)}:{(caption.end % 60).toFixed(1).padStart(4, '0')}
                            </div>
                            <div className="caption-text">
                              {formatCaptionText(caption.text, lang)}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Settings Panel with new accessibility features */}
          <div className="settings-panel">
            <h2 className="section-title">Customize Display</h2>

            {/* Language Selection - Updated with ranked dropdowns */}
            <div className="settings-section">
              <h3 className="settings-title">Select Languages</h3>

              {/* Primary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label">Primary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    value={selectedLanguages.primary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'primary')}
                    className="settings-select language-dropdown"
                  >
                    {availableTranscripts.map(transcript => (
                      <option key={transcript.language_code} value={transcript.language_code}>
                        {transcript.language}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Secondary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label">Secondary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    value={selectedLanguages.secondary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'secondary')}
                    className="settings-select language-dropdown"
                  >
                    <option value="">None</option>
                    {availableTranscripts.map(transcript => (
                      <option key={transcript.language_code} value={transcript.language_code}>
                        {transcript.language}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Tertiary Language Dropdown */}
              <div className="language-selection-item">
                <label className="settings-label">Tertiary Language</label>
                <div className="language-dropdown-container">
                  <select 
                    value={selectedLanguages.tertiary} 
                    onChange={(e) => setLanguageRank(e.target.value, 'tertiary')}
                    className="settings-select language-dropdown"
                  >
                    <option value="">None</option>
                    {availableTranscripts.map(transcript => (
                      <option key={transcript.language_code} value={transcript.language_code}>
                        {transcript.language}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>

            {/* Caption Placement */}
            <div className="settings-section">
              <h3 className="settings-title">Global Settings</h3>

              <div className="settings-item">
                <label className="settings-label">Caption Placement</label>
                <select 
                  value={captionPlacement} 
                  onChange={(e) => setCaptionPlacement(e.target.value)}
                  className="settings-select"
                >
                  <option>Below Video</option>
                  <option>Overlay</option>
                </select>
              </div>

              {/* Overlay Position Controls - only show when Overlay is selected */}
              {captionPlacement === 'Overlay' && (
                <div className="settings-item">
                  <label className="settings-label">Default Overlay Position</label>
                  <div className="overlay-position-controls">
                    <div className="position-control-group">
                      <label className="position-label">Vertical</label>
                      <select 
                        value={overlayPosition.vertical} 
                        onChange={(e) => setOverlayPosition({...overlayPosition, vertical: e.target.value})}
                        className="settings-select position-select"
                      >
                        <option value="top">Top</option>
                        <option value="center">Middle</option>
                        <option value="bottom">Bottom</option>
                      </select>
                    </div>

                    <div className="position-control-group">
                      <label className="position-label">Horizontal</label>
                      <select 
                        value={overlayPosition.horizontal} 
                        onChange={(e) => setOverlayPosition({...overlayPosition, horizontal: e.target.value})}
                        className="settings-select position-select"
                      >
                        <option value="left">Left</option>
                        <option value="center">Center</option>
                        <option value="right">Right</option>
                      </select>
                    </div>
                  </div>
                </div>
              )}

              {/* High Contrast Mode Toggle - modernized */}
              <div className="settings-item">
                <div className="toggle-container">
                  <label className="toggle-switch">
                    <input
                      type="checkbox"
                      checked={highContrastMode}
                      onChange={() => setHighContrastMode(!highContrastMode)}
                    />
                    <span className="toggle-slider"></span>
                  </label>
                  <span className="toggle-label">High Contrast Mode</span>
                </div>
              </div>

              {/* Parts of Speech Highlighting - compact version */}
              <div className="settings-item">
                <label className="settings-label">Parts of Speech Colors</label>
                <div className="pos-color-controls">
                  <div className="pos-color-item">
                    <span className="pos-label">Nouns</span>
                    <select 
                      value={nounColor} 
                      onChange={(e) => setNounColor(e.target.value)}
                      className="settings-select pos-select"
                    >
                      <option>Electric Blue</option>
                      <option>Cherry Red</option>
                      <option>Forest Green</option>
                      <option>Sunny Yellow</option>
                      <option>Royal Purple</option>
                    </select>
                  </div>

                  <div className="pos-color-item">
                    <span className="pos-label">Verbs</span>
                    <select 
                      value={verbColor} 
                      onChange={(e) => setVerbColor(e.target.value)}
                      className="settings-select pos-select"
                    >
                      <option>Electric Blue</option>
                      <option>Cherry Red</option>
                      <option>Forest Green</option>
                      <option>Sunny Yellow</option>
                      <option>Royal Purple</option>
                    </select>
                  </div>

                  <div className="pos-color-item">
                    <span className="pos-label">Adjectives</span>
                    <select 
                      value={adjectiveColor} 
                      onChange={(e) => setAdjectiveColor(e.target.value)}
                      className="settings-select pos-select"
                    >
                      <option>Electric Blue</option>
                      <option>Cherry Red</option>
                      <option>Forest Green</option>
                      <option>Sunny Yellow</option>
                      <option>Royal Purple</option>
                    </select>
                  </div>
                </div>
              </div>
            </div>

            {/* Language-specific customization */}
            <div className="settings-section">
              <h3 className="settings-title">Language Customizations</h3>

              {getSelectedLanguagesArray().map(lang => (
                <div key={`customize-${lang}`} className="language-customization-card">
                  <div className="language-card-header">
                    <span>{getLanguageName(lang)}</span>
                    {lang === selectedLanguages.primary && <span className="language-tag primary">Primary</span>}
                    {lang === selectedLanguages.secondary && <span className="language-tag secondary">Secondary</span>}
                    {lang === selectedLanguages.tertiary && <span className="language-tag tertiary">Tertiary</span>}
                  </div>

                  <div className="language-card-content">
                    {/* Font Type */}
                    <div className="settings-item">
                      <label className="settings-label">Font</label>
                      <select 
                        value={languageCustomizations[lang]?.fontFamily || 'Arial / Helvetica'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'fontFamily', e.target.value)}
                        className="settings-select"
                      >
                        <optgroup label="Sans-serif">
                          <option>Arial / Helvetica</option>
                          <option>Roboto</option>
                          <option>Open Sans</option>
                          <option>Noto Sans</option>
                          <option>Verdana</option>
                        </optgroup>
                        <optgroup label="Monospace">
                          <option>Courier New</option>
                          <option>Consolas</option>
                          <option>Source Code Pro</option>
                        </optgroup>
                      </select>
                    </div>

                    {/* Font Size */}
                    <div className="settings-item">
                      <label className="settings-label">Size</label>
                      <select 
                        value={languageCustomizations[lang]?.fontSize || 'Medium'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'fontSize', e.target.value)}
                        className="settings-select"
                      >
                        <option>Small</option>
                        <option>Medium</option>
                        <option>Large</option>
                        <option>X-Large</option>
                      </select>
                    </div>

                    {/* Text Color */}
                    <div className="settings-item">
                      <label className="settings-label">Color</label>
                      <select 
                        value={languageCustomizations[lang]?.textColor || '#000000'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'textColor', e.target.value)}
                        className="settings-select"
                      >
                        <option value="#000000">Black</option>
                        <option value="#FFFFFF">White</option>
                        <option value="#333333">Dark Gray</option>
                        <option value="#1A1AFF">Dark Blue</option>
                      </select>
                    </div>

                    {/* Character Spacing */}
                    <div className="settings-item">
                      <label className="settings-label">Character Spacing</label>
                      <select 
                        value={languageCustomizations[lang]?.letterSpacing || 'normal'} 
                        onChange={(e) => updateLanguageCustomization(lang, 'letterSpacing', e.target.value)}
                        className="settings-select"
                      >
                        <option value="normal">Normal</option>
                        <option value="0.05em">Slightly Expanded</option>
                        <option value="0.1em">Expanded</option>
                        <option value="-0.03em">Condensed</option>
                      </select>
                    </div>

                    {/* Reset Button */}
                    <button 
                      className="reset-button"
                      onClick={() => {
                        // Reset this language's customizations to defaults
                        setLanguageCustomizations(prev => ({
                          ...prev,
                          [lang]: {
                            fontFamily: 'Arial / Helvetica',
                            fontSize: 'Medium',
                            textColor: '#000000',
                            letterSpacing: 'normal'
                          }
                        }));
                      }}
                    >
                      Reset
                    </button>
                  </div>
                </div>
              ))}
            </div>

            {/* Export button */}
            <div className="export-section">
              <button 
                className="export-button"
                onClick={() => {
                  // Export captions with customization settings
                  const exportData = {
                    captions,
                    customizations: languageCustomizations,
                    selectedLanguages,
                    videoId
                  };

                  const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
                  const url = URL.createObjectURL(blob);
                  const a = document.createElement('a');
                  a.href = url;
                  a.download = `captions-${videoId}.json`;
                  a.click();
                }}
              >
                Export Captions as JSON
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Additional CSS for overlay positioning is in App.css */}
    </div>
  );
}

export default App;
